LD_naive <- function(x,y) {
  if (nchar(x)==0){
    return( nchar(y) )
  } else if (nchar(y)==0){
    return( nchar(x) )
  } else if (substr(x,nchar(x),nchar(x))==substr(y,nchar(y),nchar(y))){
    return (
      Recall(substr(x,1,nchar(x)-1),substr(y,1,nchar(y)-1))
    )
  } else {
    return(
      1+
      min(
        Recall(substr(x,1,nchar(x)-1),y),
        Recall(x,substr(y,1,nchar(y)-1)),
        Recall(substr(x,1,nchar(x)-1),substr(y,1,nchar(y)-1))
      )
    )
  }
}

